CREATE TABLE t1 (id INTEGER PRIMARY KEY, name TEXT)
CREATE TABLE t2 (id INTEGER, value REAL)
CREATE INDEX idx1 ON t1(name)
DROP INDEX idx1
CREATE UNIQUE INDEX idx2 ON t1(id)
SELECT * FROM t1, t2
SELECT * FROM t1 JOIN t2 ON t1.id = t2.id
SELECT * FROM t1 LEFT JOIN t2 ON t1.id = t2.id
SELECT * FROM t1 CROSS JOIN t2
SELECT * FROM (SELECT * FROM t1) AS subq
SELECT * FROM t1 WHERE id IN (SELECT id FROM t2)
SELECT EXISTS(SELECT * FROM t1)
SELECT 1 WHERE EXISTS(SELECT * FROM t1)
INSERT INTO t1 VALUES (1, 'test')
INSERT INTO t1 (name) VALUES ('test')
INSERT INTO t1 (id, name) VALUES (1, 'a'), (2, 'b')
UPDATE t1 SET name = 'new' WHERE id = 1
DELETE FROM t1 WHERE id > 10
SELECT DISTINCT name FROM t1
SELECT name, COUNT(*) FROM t1 GROUP BY name
SELECT name, COUNT(*) FROM t1 GROUP BY name HAVING COUNT(*) > 1
SELECT name FROM t1 ORDER BY name ASC, id DESC
SELECT * FROM t1 LIMIT 10
SELECT * FROM t1 LIMIT 10 OFFSET 20
